mapper.select:
select TL.idx,P_idx,title,content,deadline,`name`,state,M_idx from TODOList AS TL
JOIN Category AS c ON C_idx=c.idx
where P_idx=#{pIdx}

    AND M_idx=0
    AND TL.C_idx = #{cIdx}

select TL.idx,P_idx,title,content,deadline,`name`,state,M_idx from TODOList AS TL
JOIN Category AS c ON C_idx=c.idx
where M_idx=#{idx}
